example.code:

var settings = 
    new NpmInstallSettings 
    {
        WorkingDirectory = "c:\myproject"
    };
NpmInstall(settings);


var settings = 
    new NpmInstallSettings 
    {
        Force = true
    };
NpmInstall(settings);


var settings = 
    new NpmInstallSettings 
    {
        Global = true
    };
settings.AddPackage("gulp");
NpmInstall(settings);


var settings = 
    new NpmInstallSettings 
    {
        Production = true
    };
NpmInstall(settings);


var settings = 
    new NpmInstallSettings 
    {
        LogLevel = NpmLogLevel.Verbose
    };
NpmInstall(settings);


var settings = 
    new NpmPackSettings 
    {
        LogLevel = NpmLogLevel.Verbose
    };
NpmPack(settings);


var settings = 
    new NpmPublishSettings 
    {
        LogLevel = NpmLogLevel.Verbose,
        Source = "c:\mypackagesource"
    };
NpmPublish(settings);


var settings = 
    new NpmRebuildSettings 
    {
        WorkingDirectory = "c:\myproject"
    };
NpmRebuild(settings);


var settings = 
    new NpmRebuildSettings 
    {
        Global = true
    };
settings.AddPackage("gulp");
NpmRebuild(settings);


var settings = 
    new NpmRunScriptSettings 
    {
        ScriptName = "hello"
        LogLevel = NpmLogLevel.Verbose
    };
NpmRunScript(settings);
